mapper.select:
select * from treatment


        concat(treatment_name,manufacturer) like "%${keyword}%"



        and  treatment_name=#{treatmentName}


     and  treatment_id=#{treatmentId}



        and   manufacturer=#{manufacturer}



        and   treatment_status=#{treatmentStatus}


        and   treatment_category=#{treatmentCategory}


        and   drug_code=#{drugCode}


        and   storage>=#{storage}


        and expired_time >= #{expiredTime}


        and expired_time <= date_add(#{expiredTime1},interval 1 day)

select vb.visitor_bill_id ,vi.visitor_id ,vi.visitor_name ,vi.gender ,u.unit_name , e.employee_name ,t.drug_code ,t.treatment_name ,vb.drug_count,t.specification ,vb.order_date from visitor_info vi,treatment t,visitor_bill vb,unit u,employee e
vi.visitor_id=vb.visitor_id and vb.treatment_id=t.treatment_id and u.unit_id=vi.unit_id and vi.doctor_id=e.employee_id and t.treatment_category=1 and vb.manipulate_status=1 and vb.payment_status=2
and vi.visitor_id=#{visitorId}
and vb.order_date >= #{orderDate}
and vb.order_date <= date_add(#{orderDate1},interval 1 day)

select DISTINCT vi.visitor_id,vi.visitor_name from visitor_info vi,visitor_bill vb,treatment t

    vi.visitor_id=vb.visitor_id and vb.treatment_id=t.treatment_id and t.treatment_category=1 and
    vb.payment_status=2 and vb.manipulate_status=1

        and vi.visitor_id=#{visitorId}


        and vb.order_date >= #{orderDate}


        and vb.order_date <= date_add(#{orderDate1},interval 1 day)

mapper.update:
update visitor_bill set manipulate_status = 2,dispense_time=now()

    visitor_bill_id in

        #{vbid}

mapper.select:
select DISTINCT pi.patient_id,pi.patient_name from patient_info pi,patient_bill pb,treatment t

    pi.patient_id=pb.patient_id and pb.treatment_id=t.treatment_id and t.treatment_category=1
     and pb.payment_status=2 and pb.manipulate_status=1

        and pi.patient_id=#{patientId}


        and pb.order_date >= #{orderDate}


        and pb.order_date <= date_add(#{orderDate1},interval 1 day)

select pb.patient_bill_id ,pi.patient_id ,pi.patient_name ,pi.gender ,u.unit_name ,e.employee_name ,t.drug_code , t.treatment_name ,pb.drug_count ,t.specification ,pb.order_date from patient_info pi,patient_bill pb,unit u,employee e,treatment t
pi.patient_id=pb.patient_id and pb.treatment_id=t.treatment_id and pi.unit_id=u.unit_id and pi.doctor_id=e.employee_id and pb.payment_status=2 and t.treatment_category=1 and pb.manipulate_status=1
and pi.patient_id=#{patientId}
and pb.order_date >= #{orderDate}
and pb.order_date <= date_add(#{orderDate1},interval 1 day)

mapper.update:
update patient_bill set manipulate_status = 2,dispense_time=now()

    patient_bill_id in

        #{pbid}

mapper.select:
select DISTINCT vi.visitor_id,vi.visitor_name from visitor_info vi,visitor_bill vb,treatment t

    vi.visitor_id=vb.visitor_id and vb.treatment_id=t.treatment_id and t.treatment_category=1 and
    vb.payment_status=2 and vb.manipulate_status=2

        and vi.visitor_id=#{visitorId}


        and vb.dispense_time >= #{dispenseTime}


        and vb.dispense_time <= date_add(#{dispenseTime1},interval 1 day)

select vb.visitor_bill_id ,vi.visitor_id ,vi.visitor_name ,vi.gender ,u.unit_name , e.employee_name ,t.drug_code ,t.treatment_name ,vb.drug_count,t.specification ,vb.dispense_time from visitor_info vi,treatment t,visitor_bill vb,unit u,employee e
vi.visitor_id=vb.visitor_id and vb.treatment_id=t.treatment_id and u.unit_id=vi.unit_id and vi.doctor_id=e.employee_id and t.treatment_category=1 and vb.manipulate_status=2 and vb.payment_status=2
and vi.visitor_id=#{visitorId}
and vb.dispense_time >= #{dispenseTime}
and vb.dispense_time <= date_add(#{dispenseTime1},interval 1 day)

select DISTINCT pi.patient_id,pi.patient_name from patient_info pi,patient_bill pb,treatment t

    pi.patient_id=pb.patient_id and pb.treatment_id=t.treatment_id and t.treatment_category=1
    and  pb.payment_status<>1 and pb.manipulate_status=2

        and pi.patient_id=#{patientId}


        and pb.dispense_time >= #{dispenseTime}


        and pb.dispense_time <= date_add(#{dispenseTime1},interval 1 day)

select pb.patient_bill_id ,pi.patient_id ,pi.patient_name ,pi.gender ,u.unit_name ,e.employee_name ,t.drug_code , t.treatment_name ,pb.drug_count ,t.specification ,pb.dispense_time from patient_info pi,patient_bill pb,unit u,employee e,treatment t
pi.patient_id=pb.patient_id and pb.treatment_id=t.treatment_id and pi.unit_id=u.unit_id and pi.doctor_id=e.employee_id and pb.payment_status<>1 and t.treatment_category=1 and pb.manipulate_status=2
and pi.patient_id=#{patientId}
and pb.dispense_time >= #{dispenseTime}
and pb.dispense_time <= date_add(#{dispenseTime1},interval 1 day)

select * from treatment

    treatment_category <>1

      and treatment_name like "%${keyword}%"



        and  treatment_id=#{treatmentId}



        and   treatment_status=#{treatmentStatus}


        and   treatment_category=#{treatmentCategory}
